-- Tabela para armazenar os produtos de autopeças
CREATE TABLE produtos (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    nome TEXT NOT NULL,
    marca TEXT NOT NULL,
    codigo_peca TEXT NOT NULL UNIQUE,
    aplicacao TEXT,
    preco REAL NOT NULL,
    estoque INTEGER NOT NULL,
    imagem_url TEXT
);

